function normalize(str) {
  return str
    .replace(/[^\w\dÍ∞Ä-Ìû£]/g, '') // ÌäπÏàòÎ¨∏Ïûê Ï†úÍ±∞
    .replace(/\s+/g, '') // Í≥µÎ∞± Ï†úÍ±∞
    .toLowerCase();
}

// Levenshtein Í±∞Î¶¨ Í≥ÑÏÇ∞ Ìï®Ïàò
function levenshtein(a, b) {
  const m = a.length, n = b.length;
  const dp = Array.from({ length: m + 1 }, () => Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + (a[i - 1] === b[j - 1] ? 0 : 1)
      );
    }
  }
  return dp[m][n];
}

function findBestFolder(racketName, indexData) {
  const norm = normalize(racketName);
  const keys = Object.keys(indexData);

  // 1. Ï†ïÌôïÌïú Îß§Ïπ≠ Î®ºÏ†Ä ÏãúÎèÑ (Ï†ïÍ∑úÌôîÎêú ÌÇ§ÏôÄ ÎπÑÍµê)
  for (const k of keys) {
    if (norm === k) {
      return k;
    }
  }

  // 2. Ïã§Ï†ú Ìè¥ÎçîÎ™ÖÍ≥º Îß§Ïπ≠ ÏãúÎèÑ (Ï≤´ Î≤àÏß∏ Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°úÏóêÏÑú Ìè¥ÎçîÎ™Ö Ï∂îÏ∂ú)
  for (const k of keys) {
    if (indexData[k] && indexData[k].length > 0) {
      const folderName = indexData[k][0].split('/')[0]; // "ARCSABER 7 PRO/1.png" -> "ARCSABER 7 PRO"
      const normalizedFolderName = normalize(folderName);
      if (norm === normalizedFolderName) {
        return k;
      }
    }
  }

  // 3. Î∂ÄÎ∂Ñ Îß§Ïπ≠ ÏãúÎèÑ (Ìè¨Ìï® Í¥ÄÍ≥Ñ)
  for (const k of keys) {
    if (norm.includes(k) || k.includes(norm)) {
      return k;
    }
  }

  // 4. Ïã§Ï†ú Ìè¥ÎçîÎ™ÖÍ≥º Î∂ÄÎ∂Ñ Îß§Ïπ≠ ÏãúÎèÑ
  for (const k of keys) {
    if (indexData[k] && indexData[k].length > 0) {
      const folderName = indexData[k][0].split('/')[0];
      const normalizedFolderName = normalize(folderName);
      if (norm.includes(normalizedFolderName) || normalizedFolderName.includes(norm)) {
        return k;
      }
    }
  }

  // 5. Levenshtein Í±∞Î¶¨Î°ú Í∞ÄÏû• Í∞ÄÍπåÏö¥ Ìè¥Îçî ÏÑ†ÌÉù
  let minDist = Infinity;
  let bestKey = null;
  for (const k of keys) {
    const dist = levenshtein(norm, k);
    if (dist < minDist) {
      minDist = dist;
      bestKey = k;
    }
  }

  // Í±∞Î¶¨Í∞Ä ÎÑàÎ¨¥ ÌÅ¨Î©¥ Îß§Ïπ≠ÌïòÏßÄ ÏïäÏùå (ÏûÑÍ≥ÑÍ∞í: 8Î°ú Ï¶ùÍ∞Ä)
  return minDist <= 8 ? bestKey : null;
}

