function getImageCandidates(racketName) {
  // 이미지 파일명 후보 생성 (공백, 특수문자 제거, 대소문자 무시)
  const base = racketName.replace(/[^\w\d가-힣]/g, '').toLowerCase();
  // png, jpg, jpeg 모두 시도
  return [
    `/images/${base}.png`,
    `/images/${base}.jpg`,
    `/images/${base}.jpeg`
  ];
}

function imageExists(src) {
  // 이미지가 실제로 존재하는지 비동기로 확인 (슬라이더에서 사용)
  return new Promise((resolve) => {
    const img = new window.Image();
    img.onload = () => resolve(true);
    img.onerror = () => resolve(false);
    img.src = src;
  });
}

function Results({ recommendations, userLabels, onRestart }) {
  const getLabelDisplayName = (key) => {
    const labelNames = {
      PlayStyle: '플레이 스타일',
      WeightPref: '무게 선호도',
      BalancePref: '밸런스 선호도',
      ShaftFlex: '샤프트 유연성',
      GripStrength: '그립 강도',
      Level: '실력 레벨'
    };
    return labelNames[key] || key;
  };

  const formatPrice = (priceStr) => {
    if (!priceStr) return '가격 정보 없음';
    return priceStr.trim();
  };

  const formatFeatures = (features) => {
    if (!features) return '특징 정보 없음';
    return features.length > 100 ? features.substring(0, 100) + '...' : features;
  };

  const [imageMap, setImageMap] = React.useState({});

  React.useEffect(() => {
    // 추천 라켓별로 실제 존재하는 이미지 경로만 매핑
    async function fetchImages() {
      const map = {};
      for (const racket of recommendations) {
        const name = racket['종류'];
        const candidates = getImageCandidates(name);
        const found = [];
        for (const src of candidates) {
          // eslint-disable-next-line no-await-in-loop
          if (await imageExists(src)) found.push(src);
        }
        map[name] = found;
      }
      setImageMap(map);
    }
    fetchImages();
  }, [recommendations]);

  return (
    <ResultsContainer>
      <ResultsHeader>
        <ResultsTitle>🎯 당신에게 추천하는 라켓</ResultsTitle>
        <ResultsSubtitle>
          설문 결과를 바탕으로 최적의 라켓을 찾았습니다
        </ResultsSubtitle>
      </ResultsHeader>

      <UserProfileSection>
        <UserProfileTitle>📊 당신의 배드민턴 프로필</UserProfileTitle>
        <UserLabelsGrid>
          {Object.entries(userLabels).map(([key, value]) => (
            value && (
              <LabelItem key={key}>
                <LabelName>{getLabelDisplayName(key)}</LabelName>
                <LabelValue>{value}</LabelValue>
              </LabelItem>
            )
          ))}
        </UserLabelsGrid>
      </UserProfileSection>

      <RecommendationsGrid>
        {recommendations.map((racket, index) => (
          <RacketCard key={index}>
            <RankBadge rank={index + 1}>{index + 1}</RankBadge>

            <RacketName>{racket['종류']}</RacketName>

            <SimilarityScore>
              <ScoreBar>
                <ScoreFill score={racket.similarityScore} />
              </ScoreBar>
              <ScoreText>{Math.round(racket.similarityScore)}%</ScoreText>
            </SimilarityScore>

            <Price>{formatPrice(racket[' 가격 '])}</Price>

            <RacketDetails>
              <DetailItem>
                <DetailLabel>무게</DetailLabel>
                <DetailValue>{racket['무게'] || '정보 없음'}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>밸런스</DetailLabel>
                <DetailValue>{racket['밸런스 포인트'] || '정보 없음'}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>샤프트</DetailLabel>
                <DetailValue>{racket['샤프트 유연성'] || '정보 없음'}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>출시일</DetailLabel>
                <DetailValue>{racket['출시날짜'] || '정보 없음'}</DetailValue>
              </DetailItem>
            </RacketDetails>

            <Features>
              <FeaturesTitle>주요 특징</FeaturesTitle>
              <FeaturesText>{formatFeatures(racket['적용 소재 및 기술력'])}</FeaturesText>
            </Features>
            {/* 이미지 슬라이더 추가 */}
            {imageMap[racket['종류']] && imageMap[racket['종류']].length > 0 && (
              <ImageSlider images={imageMap[racket['종류']]} alt={racket['종류']} />
            )}
          </RacketCard>
        ))}
      </RecommendationsGrid>

      <ActionButtons>
        <RestartButton onClick={onRestart}>
          🔄 다시 추천받기
        </RestartButton>
        <CompareButton onClick={() => alert('비교 기능은 추후 업데이트 예정입니다!')}>
          ⚖️ 라켓 비교하기
        </CompareButton>
      </ActionButtons>
    </ResultsContainer>
  );
}

