import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const SelectorContainer = styled.div`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  max-width: 100%;
  margin: 0 auto;
  width: 100%;
  overflow-x: auto;
  box-sizing: border-box;

  /* ÏÇ¨Ïù¥ÎìúÎ∞î Í¥ëÍ≥†Í∞Ä ÎÇòÌÉÄÎÇ† Îïå Î≥∏Î¨∏Ïù¥ Í≤πÏπòÏßÄ ÏïäÎèÑÎ°ù Ï°∞Ï†ï */
  @media (min-width: 1400px) {
    max-width: calc(100% - 180px); /* ÏÇ¨Ïù¥ÎìúÎ∞î Í≥µÍ∞ÑÏùÑ Ï§ÑÏó¨ÏÑú Í¥ëÍ≥†ÏôÄ Ï†ïÌôïÌûà ÎßûÏ∂§ */
    margin-right: 20px; /* ÏÇ¨Ïù¥ÎìúÎ∞îÏôÄÏùò Í∞ÑÍ≤© */
    margin-left: 55px; /* ÏôºÏ™ΩÏúºÎ°ú 50px Ï°∞Ï†ï */
  }

  @media (max-width: 900px) {
    padding: 1rem;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
  @media (max-width: 600px) {
    padding: 0.5rem;
    border-radius: 12px;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h2`
  font-size: 2rem;
  color: #333;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  color: #666;
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const SelectionInfo = styled.div`
  background: #f8f9ff;
  border-radius: 12px;
  padding: 1rem;
  margin-bottom: 2rem;
  text-align: center;
`;

const SelectionCount = styled.div`
  font-size: 1.1rem;
  color: #667eea;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const SelectionText = styled.div`
  color: #666;
  font-size: 0.9rem;
`;

const SearchContainer = styled.div`
  margin-bottom: 2rem;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 1rem;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const FilterContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;

  @media (max-width: 600px) {
    flex-direction: column;
  }
`;

const FilterButton = styled.button`
  padding: 0.5rem 1rem;
  border: 2px solid #e0e0e0;
  border-radius: 6px;
  background: white;
  color: #666;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &.active {
    background: #667eea;
    color: white;
    border-color: #667eea;
  }

  &:hover {
    border-color: #667eea;
    color: #667eea;
  }

  &.active:hover {
    color: white;
  }
`;

const RacketsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 1.5rem;
  margin-bottom: 2rem;
  width: 100%;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
