import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 480px;
  margin: 40px auto;
  text-align: center;
  padding: 2em 1em;
  background: #f8fafe;
  border-radius: 2em;
  box-shadow: 0 8px 32px rgba(40,80,140,.05);
`;

const Illustration = styled.img`
  width: 120px;
  margin-bottom: 1.5em;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  letter-spacing: -1px;
  margin: 0 0 1rem 0;
`;

const Description = styled.p`
  font-size: 1.1em;
  color: #5a5a5a;
  margin: 0 0 1.5rem 0;
  line-height: 1.6;
`;

const StartButton = styled.button`
  font-size: 1.3em;
  padding: 0.7em 2.5em;
  background: #25C3B0;
  color: #fff;
  border-radius: 1.3em;
  border: none;
  margin: 1.5em 0 0 0;
  box-shadow: 0 2px 8px rgba(0,0,0,0.13);
  transition: 0.2s;
  cursor: pointer;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0,0,0,0.2);
  }
`;

const ReviewSection = styled.div`
  margin-top: 1.2em;
  font-size: 0.98em;
  color: #999;
  line-height: 1.4;
`;

const VisitorCount = styled.div`
  color: #7b7b7b;
  font-size: 1.1rem;
  margin-bottom: 2rem;
  span {
    color: #3578e5;
    font-weight: bold;
    font-size: 1.3rem;
  }
`;

const ShareTitle = styled.div`
  margin: 1.5rem 0 1rem 0;
  color: #444;
  font-size: 1.1rem;
`;

const ShareRow = styled.div`
  display: flex;
  gap: 1.5rem;
  margin-bottom: 2rem;
  justify-content: center;
`;

const ShareCircle = styled.button`
  width: 55px;
  height: 55px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(0,0,0,0.04);
  transition: box-shadow 0.2s;
  font-size: 1.8rem;
  font-weight: bold;
  color: #fff;
  text-decoration: none;
  background: ${({$bg}) => $bg};
  border: none;
  cursor: pointer;
  &:hover {
    box-shadow: 0 4px 16px rgba(66,133,244,0.12);
    filter: brightness(0.95);
  }
`;

const URLButton = styled.button`
  width: 55px;
  height: 55px;
  border-radius: 50%;
  background: #6c63ff;
  color: #fff;
  font-weight: bold;
  font-size: 1.8rem;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: box-shadow 0.2s;
  &:hover {
    box-shadow: 0 4px 16px rgba(108,99,255,0.18);
    filter: brightness(0.95);
  }
`;

const CopiedMsg = styled.div`
  color: #6c63ff;
  font-size: 0.9rem;
  margin-top: 0.5rem;
`;

const KAKAO_IMG = process.env.PUBLIC_URL + '/kakao.webp';

export default function LandingPage({ onStart }) {
  const [visitorCount] = useState(76267); // Í∏∞Î≥∏Í∞íÏúºÎ°ú ÏÑ§Ï†ï
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    // Kakao SDK Ï¥àÍ∏∞Ìôî
    if (window.Kakao && !window.Kakao.isInitialized()) {
      window.Kakao.init('b53d7f165df12a356594e4ffe8ba060f');
    }
  }, []);

  const shareUrl = window.location.origin;
  const shareTitle = 'Î∞∞ÎìúÎØºÌÑ¥ ÎùºÏºì Ï∂îÏ≤ú ÏÑ§Î¨∏';
  const shareText = 'ÏÑ§Î¨∏ÏùÑ ÌÜµÌï¥ ÎÇ¥Í≤å ÎßûÎäî Î∞∞ÎìúÎØºÌÑ¥ ÎùºÏºìÏùÑ Ï∂îÏ≤úÎ∞õÏïÑÎ≥¥ÏÑ∏Ïöî!';

  // Í≥µÏú† Ìï®ÏàòÎì§
  const shareToKakao = () => {
    if (window.Kakao) {
      window.Kakao.Link.sendDefault({
        objectType: 'feed',
        content: {
          title: shareTitle,
          description: shareText,
          imageUrl: `${shareUrl}/badminton-og-image.png`,
          link: {
            mobileWebUrl: shareUrl,
            webUrl: shareUrl,
          },
        },
        buttons: [
          {
            title: 'ÌÖåÏä§Ìä∏ ÏãúÏûëÌïòÍ∏∞',
            link: {
              mobileWebUrl: shareUrl,
              webUrl: shareUrl,
            },
          },
        ],
      });
    } else {
      // Kakao SDKÍ∞Ä ÏóÜÏùÑ Îïå fallback
      window.open(`https://story.kakao.com/share?url=${encodeURIComponent(shareUrl)}&text=${encodeURIComponent(shareText)}`, '_blank');
    }
  };

  const shareToFacebook = () => {
    window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(shareUrl)}&quote=${encodeURIComponent(shareText)}`, '_blank', 'width=600,height=400');
  };

  const shareToTwitter = () => {
    window.open(`https://twitter.com/intent/tweet?url=${encodeURIComponent(shareUrl)}&text=${encodeURIComponent(shareText)}&hashtags=Î∞∞ÎìúÎØºÌÑ¥,ÎùºÏºìÏ∂îÏ≤ú`, '_blank', 'width=600,height=400');
  };

  return (
    <Container>
      <Illustration 
        src="https://undraw.co/api/illustrations/99d08bc2-8fdc-4c67-9e02-1b6175a9b442" 
        alt="Î∞∞ÎìúÎØºÌÑ¥ ÏùºÎü¨Ïä§Ìä∏" 
      />
      <Title>Î∞∞ÎìúÎØºÌÑ¥ ÎùºÏºì<br />Ï∂îÏ≤ú ÌÖåÏä§Ìä∏</Title>
      <Description>
        ÎÇ¥ ÌîåÎ†àÏù¥ Ïä§ÌÉÄÏùºÏóê Îî± ÎßûÎäî ÎùºÏºì,<br />
        <b>3Î∂Ñ ÎßåÏóê Ï∞æÏïÑÎìúÎ¶ΩÎãàÎã§!</b><br />
        ÏßÄÍ∏à Î∞îÎ°ú ÏãúÏûëÌï¥ÏÑú ÎÇòÎßåÏùò ÎùºÏºìÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî.
      </Description>
      <StartButton onClick={onStart}>ÌÖåÏä§Ìä∏ ÏãúÏûëÌïòÍ∏∞ üöÄ</StartButton>
      <ReviewSection>
        <span>Ïã§Ï†ú Ïù¥Ïö©Ïûê ÌõÑÍ∏∞ ‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</span><br />
        <span>"ÎçïÎ∂ÑÏóê Îî± ÎßûÎäî ÎùºÏºì ÏÉÄÏñ¥Ïöî!"</span>
