import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const SelectorContainer = styled.div`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  max-width: 100%;
  margin: 0 auto;
  width: 100%;
  overflow-x: auto;
  box-sizing: border-box;

  /* ÏÇ¨Ïù¥ÎìúÎ∞î Í¥ëÍ≥†Í∞Ä ÎÇòÌÉÄÎÇ† Îïå Î≥∏Î¨∏Ïù¥ Í≤πÏπòÏßÄ ÏïäÎèÑÎ°ù Ï°∞Ï†ï */
  @media (min-width: 1400px) {
    max-width: calc(100% - 180px); /* ÏÇ¨Ïù¥ÎìúÎ∞î Í≥µÍ∞ÑÏùÑ Ï§ÑÏó¨ÏÑú Í¥ëÍ≥†ÏôÄ Ï†ïÌôïÌûà ÎßûÏ∂§ */
    margin-right: 20px; /* ÏÇ¨Ïù¥ÎìúÎ∞îÏôÄÏùò Í∞ÑÍ≤© */
    margin-left: 55px; /* ÏôºÏ™ΩÏúºÎ°ú 50px Ï°∞Ï†ï */
  }

  @media (max-width: 900px) {
    padding: 1rem;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
  @media (max-width: 600px) {
    padding: 0.5rem;
    border-radius: 12px;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h2`
  font-size: 2rem;
  color: #333;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  color: #666;
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const SelectionInfo = styled.div`
  background: #f8f9ff;
  border-radius: 12px;
  padding: 1rem;
  margin-bottom: 2rem;
  text-align: center;
`;

const SelectionCount = styled.div`
  font-size: 1.1rem;
  color: #667eea;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const SelectionText = styled.div`
  color: #666;
  font-size: 0.9rem;
`;

const SearchContainer = styled.div`
  margin-bottom: 2rem;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 1rem;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const FilterContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;

  @media (max-width: 600px) {
    flex-direction: column;
  }
`;

const FilterButton = styled.button`
  padding: 0.5rem 1rem;
  border: 2px solid #e0e0e0;
  border-radius: 6px;
  background: white;
  color: #666;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &.active {
    background: #667eea;
    color: white;
    border-color: #667eea;
  }

  &:hover {
    border-color: #667eea;
    color: #667eea;
  }

  &.active:hover {
    color: white;
  }
`;

const RacketsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 1.5rem;
  margin-bottom: 2rem;
  width: 100%;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 1rem;
  }

  @media (max-width: 900px) {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  }
  @media (max-width: 600px) {
    grid-template-columns: 1fr;
    gap: 0.8rem;
  }
`;

const RacketCard = styled.div`
  background: ${props => {
    const racketName = props.racketName || '';
    if (racketName.includes('ARCSABER')) {
      return props.selected ? '#f0f8ff' : '#f8f9ff'; // Ïó∞Ìïú ÌååÎûÄÏÉâ Í≥ÑÏó¥
    } else if (racketName.includes('ASTROX')) {
      return props.selected ? '#fff0f5' : '#fff5f5'; // Ïó∞Ìïú Îπ®Í∞ÑÏÉâ Í≥ÑÏó¥
    } else if (racketName.includes('NANOFLARE')) {
      return props.selected ? '#f0fff0' : '#f5fff5'; // Ïó∞Ìïú Ï¥àÎ°ùÏÉâ Í≥ÑÏó¥
    }
    return props.selected ? '#f8f9ff' : 'white'; // Í∏∞Î≥∏Í∞í
  }};
  border: 2px solid ${props => props.selected ? '#667eea' : '#e0e0e0'};
  border-radius: 12px;
  padding: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  word-break: keep-all;
  overflow-wrap: break-word;

  &:hover {
    border-color: #667eea;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.2);
  }

  ${props => props.selected && `
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.3);
  `}
  
  @media (max-width: 768px) {
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
    box-sizing: border-box;
  }
`;

const SelectionBadge = styled.div`
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  width: 24px;
  height: 24px;
  background: #667eea;
  color: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: bold;
`;

const RacketName = styled.h3`
  font-size: 1.1rem;
  color: #333;
  margin-bottom: 0.5rem;
  padding-right: 30px;
  
  @media (max-width: 768px) {
    font-size: 1rem;
    padding-right: 25px;
    word-break: keep-all;
    overflow-wrap: break-word;
  }
`;

const RacketPrice = styled.div`
  font-size: 1rem;
  font-weight: 600;
  color: #667eea;
  margin-bottom: 0.5rem;
`;

const RacketSpecs = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5rem;
  margin-bottom: 0.5rem;
`;

const SpecItem = styled.div`
  font-size: 0.8rem;
  color: #666;
`;

const SpecLabel = styled.div`
  font-weight: 500;
  margin-bottom: 0.2rem;
`;

const SpecValue = styled.div`
  color: #333;
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-top: 2rem;
`;

const Button = styled.button`
  padding: 1rem 2rem;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 500;
`;

const BackButton = styled(Button)`
  background: #f0f0f0;
  color: #666;
  
  &:hover {
    background: #e0e0e0;
    transform: translateY(-2px);
  }
`;

const CompareButton = styled(Button)`
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.4);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

function RacketSelector({ onCompare, onBack }) {
  const [rackets, setRackets] = useState([]);
  const [selectedRackets, setSelectedRackets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');

  useEffect(() => {
    // CSV ÌååÏùºÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
    fetch('/data/rackets.json')
      .then(res => res.json())
      .then(data => {
        setRackets(data);
      })
      .catch(error => {
        console.error('ÎùºÏºì Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ï§ë Ïò§Î•ò:', error);
        // Ìè¥Î∞± Îç∞Ïù¥ÌÑ∞ (Í∏∞Î≥∏Ï†ÅÏù∏ ÎùºÏºìÎì§)
        const fallbackData = [
          { 'Ï¢ÖÎ•ò': 'ARCSABER X7', ' Í∞ÄÍ≤© ': '‚Ç©229,000', 'Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏': 'Ïò¨ÎùºÏö¥ÎçîÌòï(Even balance)', 'Î¨¥Í≤å': '4U', 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±': 'Flexible', 'ÌäπÏßï': 'Ïò¨ÎùºÏö¥ÎìúÌòï, Ïú†Ïó∞Ìïú ÏÉ§ÌîÑÌä∏Î°ú ÏàòÎπÑÏôÄ Í≥µÍ≤©Ïùò Í∑†ÌòïÏóê ÌÉÅÏõîÌï®. Ï§ëÏÉÅÍ∏âÏûêÏóêÍ≤å Ï†ÅÌï©.' },
          { 'Ï¢ÖÎ•ò': 'ARCSABER 1', ' Í∞ÄÍ≤© ': '‚Ç©159,000', 'Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏': 'Ïò¨ÎùºÏö¥ÎçîÌòï(Even balance)', 'Î¨¥Í≤å': '4U', 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±': 'Stiff', 'ÌäπÏßï': 'Ïò¨ÎùºÏö¥ÎìúÌòï, Ï†ïÎ∞ÄÌïú ÏÉ∑Í≥º ÏïàÏ†ïÏ†ÅÏù∏ ÏàòÎπÑÏóê Ïú†Î¶¨. Îã®Îã®Ìïú ÏÉ§ÌîÑÌä∏.' },
          { 'Ï¢ÖÎ•ò': 'ASTROX 99 PRO', ' Í∞ÄÍ≤© ': '‚Ç©319,000', 'Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏': 'Í≥µÍ≤©Ìòï(Head heavy)', 'Î¨¥Í≤å': '4U', 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±': 'STIFF', 'ÌäπÏßï': 'Ìó§ÎìúÌó§ÎπÑ Ïä§Îß§Ïãú ÌäπÌôî ÎùºÏºì. Í≥µÍ≤© ÏßÄÌñ• ÏµúÏÉÅÍ∏â Î™®Îç∏.' },
          { 'Ï¢ÖÎ•ò': 'NANOFLARE 1000Z', ' Í∞ÄÍ≤© ': '‚Ç©339,000', 'Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏': 'Í∏∞Ïà†Ìòï(Head light)', 'Î¨¥Í≤å': '3U, 4U', 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±': 'Extra stiff', 'ÌäπÏßï': 'Í∏∞Ïà†Ìòï ÏµúÍ≥†Í∏â Î™®Îç∏. Ïä§ÌîºÎìú ÎìúÎùºÏù¥Î∏å ÌäπÌôî.' }
        ];
        setRackets(fallbackData);
      });
  }, []);

  const handleRacketSelect = (racket) => {
    const isSelected = selectedRackets.some(r => r['Ï¢ÖÎ•ò'] === racket['Ï¢ÖÎ•ò']);
    
    if (isSelected) {
      setSelectedRackets(selectedRackets.filter(r => r['Ï¢ÖÎ•ò'] !== racket['Ï¢ÖÎ•ò']));
    } else {
      if (selectedRackets.length < 3) {
        const newSelectedRackets = [...selectedRackets, racket];
        setSelectedRackets(newSelectedRackets);
        
        // 3Í∞úÍ∞Ä ÏÑ†ÌÉùÎêòÎ©¥ ÏûêÎèôÏúºÎ°ú ÎπÑÍµê ÌôîÎ©¥ÏúºÎ°ú ÎÑòÏñ¥Í∞ê
        if (newSelectedRackets.length === 3) {
          setTimeout(() => {
            onCompare(newSelectedRackets);
          }, 500); // 0.5Ï¥à ÌõÑ ÏûêÎèô Ïù¥Îèô (ÏÑ†ÌÉù Ìö®Í≥ºÎ•º Î≥º Ïàò ÏûàÎèÑÎ°ù)
        }
      } else {
        alert('ÏµúÎåÄ 3Í∞úÍπåÏßÄÎßå ÏÑ†ÌÉùÌï† Ïàò ÏûàÏäµÎãàÎã§.');
      }
    }
  };

  const handleCompare = () => {
    if (selectedRackets.length < 2) {
      alert('ÎπÑÍµêÌïòÎ†§Î©¥ ÏµúÏÜå 2Í∞úÏùò ÎùºÏºìÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    onCompare(selectedRackets);
  };

  const filteredRackets = rackets.filter(racket => {
    const matchesSearch = racket['Ï¢ÖÎ•ò'].toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterType === 'all' || 
      (filterType === 'arcsaber' && racket['Ï¢ÖÎ•ò'].includes('ARCSABER')) ||
      (filterType === 'astrox' && racket['Ï¢ÖÎ•ò'].includes('ASTROX')) ||
      (filterType === 'nanoflare' && racket['Ï¢ÖÎ•ò'].includes('NANOFLARE'));
    
    return matchesSearch && matchesFilter;
  });

  return (
    <SelectorContainer>
      <Header>
        <Title>üè∏ ÎùºÏºì ÏÑ†ÌÉù üè∏</Title>
        <Subtitle>ÎπÑÍµêÌïòÍ≥† Ïã∂ÏùÄ ÎùºÏºìÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî (ÏµúÎåÄ 3Í∞ú)</Subtitle>
      </Header>

      <SelectionInfo>
        <SelectionCount>
          {selectedRackets.length}/3 ÎùºÏºì ÏÑ†ÌÉùÎê®
        </SelectionCount>
        <SelectionText>
          {selectedRackets.length === 3 
            ? '3Í∞ú ÏÑ†ÌÉù ÏôÑÎ£å! ÏûêÎèôÏúºÎ°ú ÎπÑÍµê ÌôîÎ©¥ÏúºÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§...' 
            : selectedRackets.length >= 2 
              ? 'ÎπÑÍµêÌï† Ï§ÄÎπÑÍ∞Ä ÎêòÏóàÏäµÎãàÎã§!' 
              : 'ÏµúÏÜå 2Í∞úÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.'}
        </SelectionText>
      </SelectionInfo>

      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="ÎùºÏºì Ïù¥Î¶ÑÏúºÎ°ú Í≤ÄÏÉâ..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </SearchContainer>

      <FilterContainer>
        <FilterButton
          className={filterType === 'all' ? 'active' : ''}
          onClick={() => setFilterType('all')}
        >
          Ï†ÑÏ≤¥
        </FilterButton>
        <FilterButton
          className={filterType === 'arcsaber' ? 'active' : ''}
          onClick={() => setFilterType('arcsaber')}
        >
          ARCSABER
        </FilterButton>
        <FilterButton
          className={filterType === 'astrox' ? 'active' : ''}
          onClick={() => setFilterType('astrox')}
        >
          ASTROX
        </FilterButton>
        <FilterButton
          className={filterType === 'nanoflare' ? 'active' : ''}
          onClick={() => setFilterType('nanoflare')}
        >
          NANOFLARE
        </FilterButton>
      </FilterContainer>

      <RacketsGrid>
        {filteredRackets.map((racket, index) => {
          const isSelected = selectedRackets.some(r => r['Ï¢ÖÎ•ò'] === racket['Ï¢ÖÎ•ò']);
          const selectionIndex = selectedRackets.findIndex(r => r['Ï¢ÖÎ•ò'] === racket['Ï¢ÖÎ•ò']);
          
          return (
            <RacketCard
              key={index}
              selected={isSelected}
              racketName={racket['Ï¢ÖÎ•ò']}
              onClick={() => handleRacketSelect(racket)}
            >
              {isSelected && (
                <SelectionBadge>{selectionIndex + 1}</SelectionBadge>
              )}
              <RacketName>{racket['Ï¢ÖÎ•ò']}</RacketName>
                             <RacketPrice>{racket['Í∞ÄÍ≤©']}</RacketPrice>
              <RacketSpecs>
                <SpecItem>
                  <SpecLabel>Î∞∏Îü∞Ïä§</SpecLabel>
                  <SpecValue>{racket['Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏']}</SpecValue>
                </SpecItem>
                <SpecItem>
                  <SpecLabel>Î¨¥Í≤å</SpecLabel>
                  <SpecValue>{racket['Î¨¥Í≤å'] || 'Ï†ïÎ≥¥ ÏóÜÏùå'}</SpecValue>
                </SpecItem>
                <SpecItem>
                  <SpecLabel>ÏÉ§ÌîÑÌä∏</SpecLabel>
                  <SpecValue>{racket['ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±'] || 'Ï†ïÎ≥¥ ÏóÜÏùå'}</SpecValue>
                </SpecItem>
                <SpecItem>
                  <SpecLabel>ÌäπÏßï</SpecLabel>
                  <SpecValue>{racket['ÌäπÏßï'].substring(0, 30)}...</SpecValue>
                </SpecItem>
              </RacketSpecs>
            </RacketCard>
          );
        })}
      </RacketsGrid>

      <ActionButtons>
        <BackButton onClick={onBack}>
          ‚Üê Ïù¥Ï†ÑÏúºÎ°ú
        </BackButton>
        <CompareButton 
          onClick={handleCompare}
          disabled={selectedRackets.length < 2}
        >
          ‚öñÔ∏è ÎùºÏºì ÎπÑÍµêÌïòÍ∏∞ ({selectedRackets.length}Í∞ú)
        </CompareButton>
      </ActionButtons>
    </SelectorContainer>
  );
}

export default RacketSelector;
