import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

// Î©îÏù∏ Ïª®ÌÖåÏù¥ÎÑà (ÌÖåÏä§Ìä∏ ÏãúÏûëÌïòÍ∏∞, ÏπúÍµ¨ÏôÄ Í≥µÏú†ÌïòÍ∏∞Ïö©)
const MainContainer = styled.div`
  max-width: 480px;
  margin: 40px auto;
  text-align: center;
`;

// Í¥ëÍ≥†Ïö© Ïª®ÌÖåÏù¥ÎÑà (ÏµúÏÉÅÎã® Í¥ëÍ≥†Ïö©)
const AdContainer = styled.div`
  max-width: 1184px;
  margin: 40px auto;
  text-align: center;
  padding: 0 1rem;
`;

// Î©îÏù∏ Ïπ¥Îìú Ïª®ÌÖåÏù¥ÎÑà
const MainCard = styled.div`
  max-width: 480px;
  margin: 0 auto;
  padding: 2em 1em;
  background: #f8fafe;
  border-radius: 2em;
  box-shadow: 0 8px 32px rgba(40,80,140,.05);
  margin-bottom: 1.5rem;
`;

// ÏàòÌèâ Î∞∞Ïπò Ïª®ÌÖåÏù¥ÎÑà
const HorizontalContainer = styled.div`
  display: flex;
  gap: 1.5rem;
  justify-content: center;
  margin: 2em auto;
  width: 100%;
  max-width: 1184px;
  padding: 0 1rem;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    max-width: 480px;
  }
`;

// Ï∂îÏ≤ú ÏòàÏãú Ïπ¥Îìú
const ExampleCard = styled.div`
  background: ${props => props.$bg || '#f6fafc'};
  border-radius: 1.1em;
  box-shadow: 0 4px 16px #e2e6ee;
  padding: 1.8em 1.2em;
  width: 280px;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: transform 0.2s, box-shadow 0.2s;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0,0,0,0.12);
  }

  @media (max-width: 768px) {
    width: 100%;
    max-width: 400px;
  }
`;

const RacketImage = styled.img`
  width: 360px;
  height: 360px;
  object-fit: contain;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  margin-top: 0.5em;
`;

const RacketIcon = styled.div`
  width: 80px;
  height: 80px;
  margin-bottom: 1em;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
`;

const RacketType = styled.div`
  font-weight: bold;
  font-size: 1.3em;
  color: ${props => props.$color || '#2186eb'};
  margin-bottom: 0.5em;
  text-align: center;
`;

const RacketDescription = styled.div`
  font-size: 1.1em;
  color: #111;
  margin-bottom: 1em;
  text-align: center;
  line-height: 1.4;
`;

const RacketExample = styled.div`
  font-size: 1em;
  color: #888;
  text-align: center;
  margin-bottom: 1em;
`;

// ÏÇ¨Ïö©Ïûê ÌõÑÍ∏∞ Ïª®ÌÖåÏù¥ÎÑà
const ReviewsContainer = styled.div`
  padding: 1.8em 1.2em;
  background: #fff;
  border-radius: 1.1em;
  box-shadow: 0 4px 16px #e2e6ee;
  width: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: transform 0.2s, box-shadow 0.2s;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0,0,0,0.12);
  }

  @media (max-width: 768px) {
    width: 100%;
    max-width: 400px;
  }
`;

const ReviewsTitle = styled.h3`
  font-size: 1.3em;
  color: #333;
  margin: 0 0 1.2rem 0;
  font-weight: 600;
  text-align: center;
`;

const UserReviewItem = styled.div`
  font-size: 1.1em;
  color: #444;
  background: #f6f8fc;
  border-radius: 0.8em;
  padding: 0.8em 1em;
  margin-bottom: 0.8em;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  line-height: 1.4;

  &:last-child {
    margin-bottom: 0;
  }
`;

const UserName = styled.span`
  font-weight: bold;
  color: #333;
  font-size: 1.1em;
`;

const ReviewText = styled.span`
  color: ${props => props.$color || '#2186eb'};
  font-size: 1em;
  margin-top: 0.3em;
`;

const Description = styled.p`
  font-size: 1.1em;
  color: #5a5a5a;
  margin: 0 0 1.5rem 0;
  line-height: 1.6;
`;

const StartButton = styled.button`
  font-size: 1.3em;
  padding: 0.7em 2.5em;
  background: #25C3B0;
  color: #fff;
  border-radius: 1.3em;
  border: none;
  margin: 1.5em 0 0 0;
  box-shadow: 0 2px 8px rgba(0,0,0,0.13);
  transition: 0.2s;
  cursor: pointer;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0,0,0,0.2);
  }
`;

const ReviewSection = styled.div`
  margin-top: 1.2em;
  font-size: 0.98em;
  color: #999;
  line-height: 1.4;
`;

const ShareTitle = styled.div`
  margin: 1.5rem 0 1rem 0;
  color: #444;
  font-size: 1.1rem;
`;

const ShareRow = styled.div`
  display: flex;
  gap: 1.5rem;
  margin-bottom: 2rem;
  justify-content: center;
`;

const ShareCircle = styled.button`
  width: 55px;
  height: 55px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(0,0,0,0.04);
  transition: box-shadow 0.2s;
  font-size: 1.8rem;
  font-weight: bold;
  color: #fff;
  text-decoration: none;
  background: ${({$bg}) => $bg};
  border: none;
  cursor: pointer;
  &:hover {
    box-shadow: 0 4px 16px rgba(66,133,244,0.12);
    filter: brightness(0.95);
  }
`;

const URLButton = styled.button`
  width: 55px;
  height: 55px;
  border-radius: 50%;
  background: #6c63ff;
  color: #fff;
  font-weight: bold;
  font-size: 1.8rem;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: box-shadow 0.2s;
  &:hover {
    box-shadow: 0 4px 16px rgba(108,99,255,0.18);
    filter: brightness(0.95);
  }
`;

const CopiedMsg = styled.div`
  color: #6c63ff;
  font-size: 0.9rem;
  margin-top: 0.5rem;
`;

const KAKAO_IMG = process.env.PUBLIC_URL + '/kakao.webp';

export default function LandingPage({ onStart }) {
  const [copied, setCopied] = useState(false);
  const [randomReviews, setRandomReviews] = useState([]);

  // ÏÇ¨Ïö©Ïûê ÌõÑÍ∏∞ Î∞∞Ïó¥
  const allReviews = [
    "Ï∂îÏ≤úÎ∞õÏùÄ ÎùºÏºìÏúºÎ°ú Í≤ΩÍ∏∞Î†•Ïù¥ ÎààÏóê ÎùÑÍ≤å Ï¢ãÏïÑÏ°åÏñ¥Ïöî. Îã§ÏùåÏóêÎèÑ Íº≠ Ïù¥Ïö©Ìï† Í±∞ÏòàÏöî!",
    "ÎùºÏºìÏù¥ Í∞ÄÎ≤ºÏõåÏÑú Ïä§ÏúôÏù¥ Ìõ®Ïî¨ Î∂ÄÎìúÎü¨ÏõåÏ°åÏñ¥Ïöî. ÏÜêÎ™© Î∂ÄÎã¥Ïù¥ Ìôï Ï§ÑÏóàÎÑ§Ïöî.",
    "ÏàòÎπÑÌòï ÎùºÏºì ÎçïÎ∂ÑÏóê Îû†Î¶¨ÏóêÏÑú Ïã§ÏàòÍ∞Ä Ìôï Ï§ÑÏóàÏñ¥Ïöî. ÏûêÏã†Í∞êÎèÑ ÏÉùÍ≤ºÏäµÎãàÎã§.",
    "Í≥µÍ≤©Ìòï ÎùºÏºìÏù¥ ÏÉùÍ∞ÅÎ≥¥Îã§ Ïûò ÎßûÏïÑÏÑú Ïä§Îß§Ïãú ÏÑ±Í≥µÎ•†Ïù¥ ÎÜíÏïÑÏ°åÏñ¥Ïöî!",
    "Ïò¨ÎùºÏö¥Îìú ÎùºÏºìÏúºÎ°ú Î∞îÍæ∏Í≥† ÏπúÍµ¨Îì§Í≥ºÏùò Í≤åÏûÑÏù¥ Îçî Ïû¨ÎØ∏ÏûàÏñ¥Ï°åÏñ¥Ïöî.",
    "Ï≤òÏùåÏóêÎäî Î∞òÏã†Î∞òÏùòÌñàÎäîÎç∞, Ïã§Ï†úÎ°ú Ïç®Î≥¥Îãà ÎßåÏ°±ÎèÑÍ∞Ä ÎÜíÏïÑÏöî.",
    "Ï¥àÎ≥¥ÏûêÏóêÍ≤å ÎßûÎäî ÎùºÏºìÏùÑ ÏïåÎ†§Ï§òÏÑú Î∂ÄÎã¥ ÏóÜÏù¥ Ïó∞ÏäµÌï† Ïàò ÏûàÏóàÏäµÎãàÎã§.",
    "ÌÖåÏä§Ìä∏ Í≤∞Í≥ºÎåÄÎ°ú ÎùºÏºìÏùÑ ÏÉÄÎäîÎç∞ Í∑∏Î¶ΩÍ∞êÏù¥ Ï†ïÎßê Ï¢ãÎÑ§Ïöî.",
    "ÎùºÏºì Î¨¥Í≤åÎÇò Î∞∏Îü∞Ïä§Í∞Ä Ï†ÄÎûë Ïûò ÎßûÏïÑÏÑú Ïò§Îûú ÏãúÍ∞Ñ Ï≥êÎèÑ ÌîºÍ≥§ÌïòÏßÄ ÏïäÏïÑÏöî.",
    "Í∏∞Ï°¥Ïóê Ïì∞Îçò ÎùºÏºìÍ≥º ÎπÑÍµêÌñàÏùÑ Îïå Ìõ®Ïî¨ ÌÉÄÍµ¨Í∞êÏù¥ Î∂ÄÎìúÎüΩÏäµÎãàÎã§.",
    "Ïó¨Ïûê ÏÑ†ÏàòÏóêÍ≤å Ï∂îÏ≤úÎêú Ï†úÌíàÏù¥ÎùºÍ≥† Ìï¥ÏÑú ÏÑ†ÌÉùÌñàÎäîÎç∞ ÎÑàÎ¨¥ ÎßåÏ°±Ïä§Îü¨ÏõåÏöî.",
    "Í∞ÄÏÑ±ÎπÑ Ï¢ãÏùÄ ÎùºÏºìÏùÑ Ï∂îÏ≤úÎ∞õÏïÑ Î∂ÄÎã¥ ÏóÜÏù¥ ÏûÖÎ¨∏Ìï† Ïàò ÏûàÏóàÏñ¥Ïöî.",
    "ÎÇ¥ Ïä§ÌÉÄÏùºÏóê ÎßûÎäî ÎùºÏºìÏùÑ Ï∞æÏïÑÏ§òÏÑú Ïó∞ÏäµÏù¥ Îçî Ïû¨ÎØ∏ÏûàÏñ¥Ï°åÏñ¥Ïöî.",
    "ÎùºÏºì Î∞îÍæº ÌõÑ ÎÑ§Ìä∏ ÌîåÎ†àÏù¥Í∞Ä ÏàòÏõîÌï¥Ï†∏ÏÑú Ï†êÏ†ê Ïã§Î†•Ïù¥ ÎäêÎäî Í≤å ÎäêÍª¥ÏßëÎãàÎã§.",
    "Îî± ÏõêÌïòÎäî ÎäêÎÇåÏùò ÎùºÏºìÏùÑ Ï∂îÏ≤úÌï¥Ï§òÏÑú Í∞êÏÇ¨Ìï©ÎãàÎã§.",
    "Í≤åÏûÑÌï† Îïå ÌûòÏù¥ Îçú Îì§Ïñ¥Í∞ÄÍ≥† Ï†ïÌôïÎèÑÍ∞Ä Ï¢ãÏïÑÏßÑ ÎäêÎÇåÏù¥ÏóêÏöî.",
    "ÎîîÏûêÏù∏ÎèÑ ÏòàÏÅòÍ≥† Í∏∞Îä•ÎèÑ Ï¢ãÏïÑÏÑú ÎßåÏ°±Ïä§Îü¨ÏõåÏöî.",
    "ÏÑúÎπÑÏä§Í∞Ä Îπ®ÎùºÏÑú Í∏àÎ∞© Ï¢ãÏùÄ ÎùºÏºìÏùÑ Ï∞æÏùÑ Ïàò ÏûàÏóàÏäµÎãàÎã§.",
    "ÎùºÏºì Í≥†ÎØºÎßå ÌïòÎã§Í∞Ä ÌÖåÏä§Ìä∏ Ìïú Î≤àÏóê Í≤∞Ï†ïÌï¥ÏÑú Ìé∏ÌñàÏñ¥Ïöî!",
    "ÏπúÍµ¨ÏóêÍ≤åÎèÑ Ï∂îÏ≤úÌïòÍ≥† Ïã∂ÏùÄ ÏÑúÎπÑÏä§ÏûÖÎãàÎã§. ÎçïÎ∂ÑÏóê Ï¶êÍ≤ÅÍ≤å Ïö¥ÎèôÌïòÍ≥† ÏûàÏñ¥Ïöî."
  ];

  // ÎûúÎç§ ÌõÑÍ∏∞ ÏÑ†ÌÉù Ìï®Ïàò
  const getRandomReviews = () => {
    const shuffled = [...allReviews].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, 3);
  };

  useEffect(() => {
    // Kakao SDK Ï¥àÍ∏∞Ìôî
    if (window.Kakao && !window.Kakao.isInitialized()) {
      window.Kakao.init('b53d7f165df12a356594e4ffe8ba060f');
    }

    // Îß§Î≤à ÏÉàÎ°úÏö¥ ÎûúÎç§ ÌõÑÍ∏∞ ÏÑ§Ï†ï
    setRandomReviews(getRandomReviews());
  }, []); // Îπà ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Î°ú Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ ÏãúÏóêÎßå Ïã§Ìñâ

  const shareUrl = window.location.origin;
  const shareTitle = 'Î∞∞ÎìúÎØºÌÑ¥ ÎùºÏºì Ï∂îÏ≤ú ÏÑ§Î¨∏';
  const shareText = 'ÏÑ§Î¨∏ÏùÑ ÌÜµÌï¥ ÎÇ¥Í≤å ÎßûÎäî Î∞∞ÎìúÎØºÌÑ¥ ÎùºÏºìÏùÑ Ï∂îÏ≤úÎ∞õÏïÑÎ≥¥ÏÑ∏Ïöî!';

  // Í≥µÏú† Ìï®ÏàòÎì§
  const shareToKakao = () => {
    if (window.Kakao) {
      window.Kakao.Link.sendDefault({
        objectType: 'feed',
        content: {
          title: shareTitle,
          description: shareText,
          imageUrl: `${shareUrl}/badminton-og-image.png`,
          link: {
            mobileWebUrl: shareUrl,
            webUrl: shareUrl,
          },
        },
        buttons: [
          {
            title: 'ÌÖåÏä§Ìä∏ ÏãúÏûëÌïòÍ∏∞',
            link: {
