import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ImageSlider from './ImageSlider';

const ComparisonContainer = styled.div`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  max-width: 100%;
  margin: 0 auto;
  width: 100%;
  overflow-x: auto;
  box-sizing: border-box;

  /* ÏÇ¨Ïù¥ÎìúÎ∞î Í¥ëÍ≥†Í∞Ä ÎÇòÌÉÄÎÇ† Îïå Î≥∏Î¨∏Ïù¥ Í≤πÏπòÏßÄ ÏïäÎèÑÎ°ù Ï°∞Ï†ï */
  @media (min-width: 1400px) {
    max-width: calc(100% - 180px); /* ÏÇ¨Ïù¥ÎìúÎ∞î Í≥µÍ∞ÑÏùÑ Ï§ÑÏó¨ÏÑú Í¥ëÍ≥†ÏôÄ Ï†ïÌôïÌûà ÎßûÏ∂§ */
    margin-right: 20px; /* ÏÇ¨Ïù¥ÎìúÎ∞îÏôÄÏùò Í∞ÑÍ≤© */
    margin-left: 55px; /* ÏôºÏ™ΩÏúºÎ°ú 50px Ï°∞Ï†ï */
  }

  @media (max-width: 900px) {
    padding: 1rem;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
  @media (max-width: 600px) {
    padding: 0.5rem;
    border-radius: 12px;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h2`
  font-size: 2rem;
  color: #333;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  color: #666;
  font-size: 1.1rem;
`;

const ComparisonGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* 3Í∞ú ÎùºÏºìÏùÑ Í≥†Ï†ïÏúºÎ°ú Î∞∞Ïπò */
  gap: 2rem;
  margin-bottom: 2rem;
  width: 100%;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, 1fr); /* Ï§ëÍ∞Ñ ÌÅ¨Í∏∞ÏóêÏÑúÎäî 2Í∞úÏî© */
    gap: 1.5rem;
  }

  @media (max-width: 900px) {
    grid-template-columns: 1fr;
    gap: 1rem;
  }
`;

const RacketCard = styled.div`
  background: white;
  border: 2px solid #e0e0e0;
  border-radius: 16px;
  padding: 1.5rem;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  width: 100%;
  box-sizing: border-box;
  word-break: keep-all;
  overflow-wrap: break-word;

  @media (max-width: 900px) {
    padding: 1rem;
  }
  @media (max-width: 600px) {
    padding: 0.7rem;
    border-radius: 8px;
    margin: 0 0.5rem;
    width: calc(100% - 1rem);
  }

  &:hover {
    border-color: #667eea;
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(102, 126, 234, 0.2);
  }
`;

const RacketName = styled.h3`
  font-size: 1.4rem;
  color: #333;
  margin-bottom: 1rem;
  text-align: center;
`;

const Price = styled.div`
  font-size: 1.3rem;
  font-weight: bold;
  color: #667eea;
  margin-bottom: 1rem;
  text-align: center;
`;

const SpecsGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-bottom: 1rem;
`;

const SpecItem = styled.div`
  display: flex;
  flex-direction: column;
  background: #f8f9ff;
  padding: 0.8rem;
  border-radius: 8px;
`;

const SpecLabel = styled.span`
  font-size: 0.8rem;
  color: #666;
  margin-bottom: 0.2rem;
  font-weight: 500;
`;

const SpecValue = styled.span`
  font-size: 0.9rem;
  color: #333;
  font-weight: 600;
`;

const Features = styled.div`
  background: #f8f9ff;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const FeaturesTitle = styled.div`
  font-size: 0.9rem;
  font-weight: 500;
  color: #333;
  margin-bottom: 0.5rem;
`;

const FeaturesText = styled.div`
  font-size: 0.8rem;
  color: #666;
  line-height: 1.4;
`;

const ComparisonTable = styled.div`
  background: #f8f9ff;
  border-radius: 12px;
  padding: 1.5rem;
  margin-bottom: 2rem;
`;

const TableTitle = styled.h3`
  font-size: 1.3rem;
  color: #333;
  margin-bottom: 1rem;
  text-align: center;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: white;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

const Th = styled.th`
  background: #667eea;
  color: white;
  padding: 1rem;
  text-align: left;
  font-weight: 500;
  font-size: 0.9rem;
`;

const Td = styled.td`
  padding: 1rem;
  border-bottom: 1px solid #e0e0e0;
  font-size: 0.9rem;
  
  &:first-child {
    font-weight: 600;
    background: #f8f9ff;
  }
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-top: 2rem;
`;

const Button = styled.button`
  padding: 1rem 2rem;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 500;
`;

const BackButton = styled(Button)`
  background: #f0f0f0;
  color: #666;
  
  &:hover {
    background: #e0e0e0;
    transform: translateY(-2px);
  }
`;

const SelectMoreButton = styled(Button)`
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.4);
  }
`;

const NoRacketsMessage = styled.div`
  text-align: center;
  padding: 3rem;
  color: #666;
  font-size: 1.1rem;
`;

function RacketComparison({ selectedRackets, onBack, onSelectMore }) {
  const [imagesIndex, setImagesIndex] = useState(null);

  useEffect(() => {
    fetch('/images/index.json')
      .then(res => res.json())
      .then(setImagesIndex)
      .catch(() => setImagesIndex(null));
  }, []);

  const normalize = (str) => {
    return str
      .replace(/[^\w\dÍ∞Ä-Ìû£]/g, '')
      .replace(/\s+/g, '')
      .toLowerCase();
  };

  const findBestFolder = (racketName, indexData) => {
    const norm = normalize(racketName);
    const keys = Object.keys(indexData);
    
    for (const k of keys) {
      if (norm === k) {
        return k;
      }
    }
    
    for (const k of keys) {
      if (indexData[k] && indexData[k].length > 0) {
        const folderName = indexData[k][0].split('/')[0];
        const normalizedFolderName = normalize(folderName);
        if (norm === normalizedFolderName) {
          return k;
        }
      }
    }
    
    for (const k of keys) {
      if (norm.includes(k) || k.includes(norm)) {
        return k;
      }
    }
    
    return null;
  };

  const formatPrice = (priceStr) => {
    if (!priceStr) return 'Í∞ÄÍ≤© Ï†ïÎ≥¥ ÏóÜÏùå';
    return priceStr.trim();
  };

  const formatFeatures = (features) => {
    if (!features) return 'ÌäπÏßï Ï†ïÎ≥¥ ÏóÜÏùå';
    return features.length > 150 ? features.substring(0, 150) + '...' : features;
  };

  if (!selectedRackets || selectedRackets.length === 0) {
    return (
      <ComparisonContainer>
        <NoRacketsMessage>
          <h3>ÎπÑÍµêÌï† ÎùºÏºìÏù¥ ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§</h3>
          <p>ÎùºÏºìÏùÑ ÏÑ†ÌÉùÌïú ÌõÑ ÎπÑÍµêÌï¥Î≥¥ÏÑ∏Ïöî.</p>
          <ActionButtons>
            <SelectMoreButton onClick={onSelectMore}>
              ÎùºÏºì ÏÑ†ÌÉùÌïòÍ∏∞
            </SelectMoreButton>
          </ActionButtons>
        </NoRacketsMessage>
      </ComparisonContainer>
    );
  }

  // ÎπÑÍµê ÌÖåÏù¥Î∏îÏö© Îç∞Ïù¥ÌÑ∞ Ï§ÄÎπÑ
  const comparisonData = [
    { label: 'Í∞ÄÍ≤©', key: 'Í∞ÄÍ≤©' },
    { label: 'Î¨¥Í≤å', key: 'Î¨¥Í≤å' },
    { label: 'Î∞∏Îü∞Ïä§', key: 'Î∞∏Îü∞Ïä§ Ìè¨Ïù∏Ìä∏' },
    { label: 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±', key: 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±' },
    { label: 'Í∑∏Î¶Ω ÏÇ¨Ïù¥Ï¶à', key: 'Í∑∏Î¶Ω ÏÇ¨Ïù¥Ï¶à' },
    { label: 'ÏÉâÏÉÅ', key: 'ÏÉâÏÉÅ' },
    { label: 'Ï†ÅÏ†ï Ïû•Î†•', key: 'Ï†ÅÏ†ï Ïû•Î†•' },
    { label: 'Ï∂úÏãúÎÇ†Ïßú', key: 'Ï∂úÏãúÎÇ†Ïßú' },
    { label: 'ÏõêÏÇ∞ÏßÄ', key: 'ÏõêÏÇ∞ÏßÄ' }
  ];

  return (
