const RacketName = styled.h3`
  font-size: 1.4rem;
  color: #333;
  margin-bottom: 1rem;
  padding-right: 50px;

  @media (max-width: 768px) {
    font-size: 1.2rem;
    padding-right: 40px;
    word-break: keep-all;
    overflow-wrap: break-word;
