            mobileWebUrl: shareUrl,
            webUrl: shareUrl,
          },
        },
        buttons: [
          {
            title: 'ÌÖåÏä§Ìä∏ ÏãúÏûëÌïòÍ∏∞',
            link: {
              mobileWebUrl: shareUrl,
              webUrl: shareUrl,
            },
          },
        ],
      });
    } else {
      // Kakao SDKÍ∞Ä ÏóÜÏùÑ Îïå fallback
      window.open(`https://story.kakao.com/share?url=${encodeURIComponent(shareUrl)}&text=${encodeURIComponent(shareText)}`, '_blank');
    }
  };

  const getLabelDisplayName = (key) => {
    const labelNames = {
      PlayStyle: 'ÌîåÎ†àÏù¥ Ïä§ÌÉÄÏùº',
      WeightPref: 'Î¨¥Í≤å ÏÑ†Ìò∏ÎèÑ',
      BalancePref: 'Î∞∏Îü∞Ïä§ ÏÑ†Ìò∏ÎèÑ',
      ShaftFlex: 'ÏÉ§ÌîÑÌä∏ Ïú†Ïó∞ÏÑ±',
      GripStrength: 'Í∑∏Î¶Ω Í∞ïÎèÑ',
      Level: 'Ïã§Î†• Î†àÎ≤®'
    };
    return labelNames[key] || key;
  };

  const formatPrice = (priceStr) => {
    if (!priceStr) return 'Í∞ÄÍ≤© Ï†ïÎ≥¥ ÏóÜÏùå';
    return priceStr.trim();
  };

  const formatFeatures = (features) => {
    if (!features) return 'ÌäπÏßï Ï†ïÎ≥¥ ÏóÜÏùå';
    return features.length > 100 ? features.substring(0, 100) + '...' : features;
  };

  const [imagesIndex, setImagesIndex] = React.useState(null);

  React.useEffect(() => {
    fetch('/images/index.json')
      .then(res => res.json())
      .then(setImagesIndex)
      .catch(() => setImagesIndex(null));
  }, []);

  return (
    <>
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î Í¥ëÍ≥† */}
      <SideAdContainer>
        <ins 
          ref={sideAdRef}
          className="adsbygoogle"
          style={{display: 'block'}}
          data-ad-client="ca-pub-9588119791313794"
          data-ad-slot="2352948514"
          data-ad-format="auto"
          data-full-width-responsive="true">
        </ins>
      </SideAdContainer>

      <ResultsContainer>
        <UserProfileSection>
        <UserProfileTitle>üìä ÎãπÏã†Ïùò Î∞∞ÎìúÎØºÌÑ¥ ÌîÑÎ°úÌïÑ</UserProfileTitle>
        <UserLabelsGrid>
          {Object.entries(userLabels).map(([key, value]) => (
            value && (
              <LabelItem key={key}>
                <LabelName>{getLabelDisplayName(key)}</LabelName>
                <LabelValue>{value}</LabelValue>
              </LabelItem>
            )
          ))}
        </UserLabelsGrid>
      </UserProfileSection>

      <RecommendationsGrid>
        {recommendations.map((racket, index) => {
